var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads : []);
if (!gamepads) {
    //console.log("Gamepad is not supported in this browser");
    return;
}

var gamepad = gamepads[Number(eventsFunctionContext.getArgument("player_ID") - 1)];
if (gamepad == null) {
    //console.log("Gamepad %d is null, maybe controller is not connected", eventsFunctionContext.getArgument("player_ID"));
    return;
}


var setDeadzone = function (v) {
    // Anything smaller than this is assumed to be 0,0
    var DEADZONE = 0.2;

    if (Math.abs(v) < DEADZONE) {
        // In the dead zone, set to 0
        v = 0;

        if (v == null) {
            return 0;
        } else {
            return v;
        }

    } else {
        // We're outside the dead zone, but we'd like to smooth
        // this value out so it still runs nicely between 0..1.
        // That is, we don't want it to jump suddenly from 0 to
        // DEADZONE.

        // Remap v from
        //    DEADZONE..1 to 0..(1-DEADZONE)
        // or from
        //    -1..-DEADZONE to -(1-DEADZONE)..0

        v = v - Math.sign(v) * DEADZONE;

        // Remap v from
        //    0..(1-DEADZONE) to 0..1
        // or from
        //    -(1-DEADZONE)..0 to -1..0

        v /= (1.0 - DEADZONE);

        if (v == null) {
            return 0;
        } else {
            return v;
        }
    }
};

var stick = eventsFunctionContext.getArgument("stick").toString();
var direction = eventsFunctionContext.getArgument("direction").toString();
var axis;

switch (stick) {
    case 'LEFT':
        switch (direction) {
            case 'LEFT':
                if (setDeadzone(gamepad.axes[0]) < 0) {
                    eventsFunctionContext.returnValue = -setDeadzone(gamepad.axes[0]);
                }
                break;

            case 'RIGHT':
                if (setDeadzone(gamepad.axes[0]) > 0) {
                    eventsFunctionContext.returnValue = setDeadzone(gamepad.axes[0]);
                }
                break;

            case 'UP':
                if (setDeadzone(gamepad.axes[1]) < 0) {
                    eventsFunctionContext.returnValue = -setDeadzone(gamepad.axes[1]);
                }
                break;

            case 'DOWN':
                if (setDeadzone(gamepad.axes[1]) > 0) {
                    eventsFunctionContext.returnValue = setDeadzone(gamepad.axes[1]);
                }
                break;

            default:
                //console.log('null axe');
                eventsFunctionContext.returnValue = 1337;
                break;
        }
        break;

    case 'RIGHT':
        switch (direction) {
            case 'LEFT':
                if (setDeadzone(gamepad.axes[2]) < 0) {
                    eventsFunctionContext.returnValue = -setDeadzone(gamepad.axes[2]);
                }
                break;

            case 'RIGHT':
                if (setDeadzone(gamepad.axes[2]) > 0) {
                    eventsFunctionContext.returnValue = setDeadzone(gamepad.axes[2]);
                }
                break;

            case 'UP':
                if (setDeadzone(gamepad.axes[3]) < 0) {
                    eventsFunctionContext.returnValue = -setDeadzone(gamepad.axes[3]);
                }
                break;

            case 'DOWN':
                if (setDeadzone(gamepad.axes[3]) > 0) {
                    eventsFunctionContext.returnValue = setDeadzone(gamepad.axes[3]);
                }
                break;

            default:
                break;
        }
        break;

    default:
        break;
}

