events.inlineCode:
  window._extensionInternetStatusChecker = {
    Status: false,
    UrlBasis: 'https://example.com/',
    FrequencyCheckSeconds: 5
  };


  // Checks internet via ping method
  const checkOnlineStatus = async () => {
    try {
      const online = await fetch(window._extensionInternetStatusChecker.UrlBasis, {
        method: 'GET',
        mode: 'no-cors',
        cache: 'no-cache',
      });

      return online.status == 0;
    } catch (e) {
      return false;
    }
  };

  // Check internet if browser/device detects an event
  window.addEventListener('load', detectInternet);
  window.addEventListener('online', detectInternet);
  window.addEventListener('offline', detectInternet);
  setInterval(detectInternet, window._extensionInternetStatusChecker.FrequencyCheckSeconds*1000);

  // Update Status in a variable
  async function detectInternet() {
    if(await checkOnlineStatus()) {
        window._extensionInternetStatusChecker.Status = true;
    } else {
        window._extensionInternetStatusChecker.Status = false;
    }
  }

  detectInternet();
