events.inlineCode:
const promptResponse = runtimeScene.getVariables().get("__PopUp").getChild("Prompt").getAsString();

if (promptResponse != "") {
    eventsFunctionContext.returnValue = true;
} else {
    eventsFunctionContext.returnValue = false;
}

const message = eventsFunctionContext.getArgument("Message");
const defaultText = eventsFunctionContext.getArgument("DefaultText");

const response = window.prompt(message, defaultText);

const childVar = new gdjs.Variable();

if (response !== null) {
    childVar.setString(response);
} else {
    childVar.setString("");
}

runtimeScene.getVariables().get("__PopUp").addChild("Prompt", childVar);

const isConfirmed = confirm(eventsFunctionContext.getArgument("Message"));

const childVar = new gdjs.Variable();
childVar.setBoolean(isConfirmed);

runtimeScene.getVariables().get("__PopUp").addChild("Confirm", childVar);
