events.inlineCode:
if (eventsFunctionContext.getArgument("ChangeResource")) {
    const texture = PIXI.BaseTexture.from(eventsFunctionContext.getArgument("URL"));
    for (const obj of objects) obj.getRendererObject().texture.baseTexture = texture;
} else {
    const texture = PIXI.Texture.from(eventsFunctionContext.getArgument("URL"));
    for (const obj of objects) obj.getRendererObject().texture = texture;
}

const resourceName = eventsFunctionContext.getArgument("Resource");
const soundManager = runtimeScene
    .getGame()
    .getSoundManager();
const resource = soundManager
    ._availableResources[resourceName];

resource.file = eventsFunctionContext.getArgument("URL");

// Unload the previous howler sound
soundManager.unloadAudio(resourceName, true);
soundManager.unloadAudio(resourceName, false);

if (resource.preloadAsMusic) soundManager.loadAudio(resourceName, true);
if (resource.preloadAsSound) soundManager.loadAudio(resourceName, false);
