var speech = new SpeechSynthesisUtterance();
var voices = [];
var __firstVoice = runtimeScene.getVariables().get("__FirstVoice");

var voice = eventsFunctionContext.getArgument("Voice");
var volume = eventsFunctionContext.getArgument("Volume");
var rate = eventsFunctionContext.getArgument("Rate");
var pitch = eventsFunctionContext.getArgument("Pitch");

// sanity checks for volume, rate, pitch
if ((volume < 0) || (1 < volume))
    volume = 1;
if ((rate < 0.1) || (10 < rate))
    rate = 1;
if ((pitch < 0) || (2 < pitch))
    pitch = 1;

// initialize speech settings
speech.lang = "en";
speech.volume = volume;
speech.rate = rate;
speech.pitch = pitch;
speech.text = eventsFunctionContext.getArgument("Message");

// get voice and start speaking.
window.speechSynthesis.onvoiceschanged = function() {
    voices = window.speechSynthesis.getVoices();

    speech.voice = voices[names.indexOf(voice)];
    window.speechSynthesis.speak(speech);

    __firstVoice.setValue(1);
};

if (__firstVoice.getValue() == 1){
    voices = window.speechSynthesis.getVoices();

    speech.voice = voices[names.indexOf(voice)];
    window.speechSynthesis.speak(speech);
}
