gdjs.evtTools.geolocation = {
    permission: { state: navigator.permissions ? "loading" : "unknown" }
};

// Prefetch the permission and set up an event handler to keep it up to date
if (navigator.permissions) navigator.permissions.query({ name: 'geolocation' })
    .then((permission) => {
        gdjs.evtTools.geolocation.permission = permission;
    });

this.logger = (this.logger || new gdjs.Logger("Geolocation extension"))

if (navigator && navigator.geolocation) navigator
    .geolocation
    .watchPosition(
        location => {
            const variable = eventsFunctionContext.getArgument("callback");
            for (const child of Object.keys(location.coords.__proto__)) {
                variable.getChild(child).setNumber(location.coords[child]);
            }
        },
        (error) => this.logger.error(`Couldn't locate player: ` + error.message),
        { enableHighAccuracy: true }
    );

this.logger = (this.logger || new gdjs.Logger("Geolocation extension"))

if (navigator && navigator.geolocation) navigator
    .geolocation
    .getCurrentPosition(
        location => {
            const variable = eventsFunctionContext.getArgument("callback");
            for (const child in location.coords) {
                variable.getChild(child).setNumber(location.coords[child]);
            }
        },
        (error) => this.logger.error(`Couldn't locate player: ` + error.message),
        { enableHighAccuracy: true }
    );
