const AppId = eventsFunctionContext.getArgument("AppId");
const EncryptKey = eventsFunctionContext.getArgument("EncryptKey");

window._newgrounds.PlayerName = null;
window._newgrounds.IsLoggedIn = false;

function OnLoggedIn() {
    window._newgrounds.ngio.getSessionLoader().closePassport();
    window._newgrounds.PlayerName = window._newgrounds.ngio.user.name;
	window._newgrounds.IsLoggedIn = window._newgrounds.ngio.user.name;
}

window._newgrounds.ngio =  new window._newgrounds.Newgrounds.io.core(
	AppId,
	EncryptKey
);

window._newgrounds.ngio.getValidSession(() => {
	if(window._newgrounds.ngio.user != null) {
		OnLoggedIn();
	}
});

function OnLoggedOut() {
    window._newgrounds.IsLoggedIn = false;
    window._newgrounds.PlayerName = null;
	window._newgrounds.ngio.getSessionLoader().closePassport();
}

function OnLoggedIn() {
    window._newgrounds.ngio.getSessionLoader().closePassport();
    window._newgrounds.PlayerName = window._newgrounds.ngio.user.name;
    window._newgrounds.IsLoggedIn = true;
}

window._newgrounds.ngio.getValidSession(() => {
	if(window._newgrounds.ngio.user != null) {
		OnLoggedIn();
	} else {
        window._newgrounds.ngio.requestLogin(OnLoggedIn, OnLoggedOut, OnLoggedOut);
    }
});

if(window._newgrounds.ScoreBoardList == undefined) {
	window._newgrounds.ScoreBoardList = [];
	window._newgrounds.ngio.callComponent("ScoreBoard.getBoards", {}, (Result) => {
		for (var i = 0; i < Result.scoreboards.length; i++) {
			window._newgrounds.ScoreBoardList.push({
				id: Result.scoreboards[i].id,
				name: Result.scoreboards[i].name
			})
		}
	});
}

eventsFunctionContext.returnValue = JSON.stringify(window._newgrounds.ScoreBoardList);

if(window._newgrounds.MedalList == undefined) {
	window._newgrounds.MedalList = [];
	window._newgrounds.ngio.callComponent("Medal.getList", {}, (Result) => {
		for (var i = 0; i < Result.medals.length; i++) {
			window._newgrounds.MedalList.push({
				id: Result.medals[i].id,
				name: Result.medals[i].name
			})
		}
	});
}

eventsFunctionContext.returnValue = JSON.stringify(window._newgrounds.MedalList);

if(window._newgrounds.Version == undefined) {
    window._newgrounds.Version = 'pending';
    window._newgrounds.ngio.callComponent('Gateway.getVersion', {}, (Result) => {
        window._newgrounds.Version = Result.version;
    });
}

eventsFunctionContext.returnValue = window._newgrounds.Version;

if(window._newgrounds.DateTime == undefined) {
    window._newgrounds.DateTime = 'pending';
    window._newgrounds.ngio.callComponent('Gateway.getDatetime', {}, (Result) => {
        window._newgrounds.DateTime = Result.datetime;
    });
}

eventsFunctionContext.returnValue = window._newgrounds.DateTime;
