events.inlineCode:
const remote = runtimeScene.getGame().getRenderer().getElectronRemote
    ? runtimeScene.getGame().getRenderer().getElectronRemote()
    : runtimeScene.getGame().getRenderer().getElectron() && runtimeScene.getGame().getRenderer().getElectron().remote;

if (!remote) return;
const { Client } = remote.require("@xhayper/discord-rpc");

if (gdjs.evtTools.discordRP.client) gdjs.evtTools.discordRP.client.destroy();
gdjs.evtTools.discordRP.client = new Client({ clientId: eventsFunctionContext.getArgument("clientId") });
gdjs.evtTools.discordRP.client.on("error", (e) => gdjs.Logger.getLoggerOutput().log("Discord Rich Presence", e));
gdjs.evtTools.discordRP.client.login().catch((e) => gdjs.Logger.getLoggerOutput().log("Discord Rich Presence", e));

window.addEventListener("beforeunload", () => gdjs.evtTools.discordRP.client.destroy());

if (!gdjs.evtTools.discordRP.client || !gdjs.evtTools.discordRP.client.user) return;

const payload = {
    state: undefined,
    details: undefined,
    startTimestamp: undefined,
    endTimestamp: undefined,
    largeImageKey: undefined,
    largeImageText: undefined,
    smallImageKey: undefined,
    smallImageText: undefined,
};

for (const i in payload) {
    payload[i] = eventsFunctionContext.getArgument(i);
    if (payload[i] === 0 || payload[i] === "") delete payload[i];
};

gdjs.evtTools.discordRP.client.user.setActivity(payload).catch((e) => gdjs.Logger.getLoggerOutput().log("Discord Rich Presence", e));
