events.inlineCode:
const convString = eventsFunctionContext.getArgument("String");
const convBase = eventsFunctionContext.getArgument("Base");
const convRange = eventsFunctionContext.getArgument("Range");

//This gets the decimal unicode and turns it into the desired base
let result = convString.split("").map(function (char) {
  return char.charCodeAt(0).toString(convBase).padStart(convRange, "0");
}).join(" ");

//This reverses the unicode representation
const joinArray = result.split("").reverse().join("");

//This turns the unicode representation into the decimal unicode representation 
//and turns it into the string using the fromCharCode function
result = "";
joinArray.split(" ").map(function (bin) {
  result += String.fromCharCode(parseInt(bin, convBase));
});

eventsFunctionContext.returnValue = result;

