const convBase = eventsFunctionContext.getArgument("Base");
let sep = eventsFunctionContext.getArgument("Sep");

//Checks if the Sperator text is empty. If it is , this sets the value of the sep variable with " "
if (sep == "") {
  sep = " ";
};

let result = "";

//This turns the unicode representation into decimal unicode representation and turns it into the string using the fromCharCode function
convString.split(sep).map(function (bin) {
  result += String.fromCharCode(parseInt(bin, convBase));
});

eventsFunctionContext.returnValue = result;

